package adventofcode2020

class App {
    val greeting: String
        get() {
            return "Hello World!"
        }
}

fun main(args: Array<String>) {
    println(App().greeting)
    val fr = FileReader(args[0])
    val lines = fr.lines()
    val tp = TupleParser(lines)
    val count = tp.count()
    println(count)
}
